IF NOT EXISTS (SELECT 1 FROM INFORMATION_SCHEMA.COLUMNS WHERE TABLE_NAME = 'Tests' AND COLUMN_NAME = 'TimeLimit') BEGIN ALTER TABLE Tests ADD TimeLimit INT NOT NULL DEFAULT 15; END IF NOT EXISTS (SELECT 1 FROM INFORMATION_SCHEMA.COLUMNS WHERE TABLE_NAME = 'Tests' AND COLUMN_NAME = 'OrderInLevel') BEGIN ALTER TABLE Tests ADD OrderInLevel INT NOT NULL DEFAULT 1; END
